import "./profile.css";
import Topbar from "../../components/topbar/Topbar";
import {useContext, useEffect, useState} from "react";
import axios from "axios";
import { useParams } from "react-router";
import { useHistory } from "react-router-dom";
import {AuthContext} from "../../context/AuthContext";

export default function Profile() {
  const PF = process.env.REACT_APP_PUBLIC_FOLDER;
  const { user: currentUser } = useContext(AuthContext);
  let [user, setUser] = useState({});
  let username = useParams().username;
  if (username == null){
    username = currentUser.username;
  }

  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get(`/users?username=${username}`);
      setUser(res.data);
    };
    fetchUser();
  }, [username]);



  const history = useHistory();

  return (
    <>
      <Topbar />
      <div className="main-content">
        {/* Header */}
        <div
          className="header pb-8 pt-5 pt-lg-8 d-flex align-items-center"
          style={{
            minHeight: 600,
            backgroundImage:
              "url(https://cdn.mos.cms.futurecdn.net/TFWk6Zp8rvbYangVbtQ9ek.jpg)",
            backgroundSize: "cover",
            backgroundPosition: "center top"
          }}
        >
          {/* Mask */}
          <span className="mask bg-gradient-default opacity-8" />
          {/* Header container */}
          <div className="container-fluid d-flex align-items-center">
            <div className="row">
              { currentUser == null ? (<span></span>) : (
                  user.username !== currentUser.username ? (
                      <span></span>
                  ) : (
              <div className="col-lg-7 col-md-10">
                <button className="btn btn-info" onClick={() => history.push(`/edit-profile/`)}>Edit Profile</button>
              </div>))}
            </div>
          </div>
        </div>
        {/* Page content */}
        <div className="container-fluid mt--7">
          <div className="row">
            <div className="col-xl-4 order-xl-2 mb-5 mb-xl-0">
              <div className="card card-profile shadow">
                <div className="row justify-content-center">
                  <div className="col-lg-3 order-lg-2">
                    <div className="card-profile-image">
                      <a >
                        <img
                          src={user.profilePicture
                            ? PF + user.profilePicture
                            : PF + "person/noAvatar.png"}
                          className="rounded-circle"
                        />
                      </a>
                    </div>
                  </div>
                </div>
                <div className="card-header text-center border-0 pt-8 pt-md-4 pb-0 pb-md-4">
                  <div className="d-flex justify-content-between">

                  </div>
                </div>
                <div className="card-body pt-0 pt-md-4">
                  <div className="row">
                    <div className="col">
                      <div className="card-profile-stats d-flex justify-content-center mt-md-5">

                      </div>
                    </div>
                  </div>
                  <div className="text-center">
                    <h3>
                      {user.username}
                      <span className="font-weight-light">,
                        {user.age != null ? (<span>{user.age}</span>) : (<span>mystery age</span>)}
                      </span>
                    </h3>
                    <div className="h5 font-weight-300">
                      <i className="ni location_pin mr-2" />
                      {user.city != null ? (<span>{user.city}, {user.country}</span>) : (<span>Mars</span>)}
                    </div>

                    <hr className="my-4" />
                    <p>
                      {user.desc != null ? (<span>{user.desc}</span>) : (<span>No information disclosed</span>)}
                    </p>

                  </div>
                </div>
              </div>
            </div>

            <div className="col-xl-8 order-xl-1">
                <form>
                  <div className="card bg-secondary shadow">
                    <div className="card-header bg-white border-0">
                      <div className="row align-items-center">
                        <div className="col-8">
                          <h3 className="mb-0">Profile Details</h3>
                        </div>

                      </div>
                    </div>
                    <div className="card-body">
                      <form>
                        <h6 className="heading-small text-muted mb-4">
                          User information
                        </h6>
                        <div className="pl-lg-4">
                          <div className="row">
                            <div className="col-lg-6">
                              <div className="form-group focused">
                                <label
                                  className="form-control-label"
                                  htmlFor="input-username"
                                >
                                  Username
                                </label>
                                <p
                                  id="input-username"
                                  className="form-control form-control-alternative">
                                  {user.username}
                                </p>
                              </div>
                            </div>
                            { currentUser == null ? (<span></span>) : (
                                user.username !== currentUser.username ? (
                                <span></span>
                            ) : (
                                <div className="col-lg-6">
                                  <div className="form-group">
                                    <label
                                        className="form-control-label"
                                        htmlFor="input-email"
                                    >
                                      Email address
                                    </label>
                                    <p
                                        id="input-email"
                                        className="form-control form-control-alternative">
                                      {user.email}
                                    </p>
                                  </div>
                                </div>
                              ))}
                          </div>
                          <div className="row">
                            { currentUser == null ? (<span></span>) : (
                              user.username !== currentUser.username ? (
                                <span></span>
                            ) : (<div className="col-lg-6">
                              <div className="form-group focused">
                                <label
                                    className="form-control-label"
                                    htmlFor="input-first-name"
                                >
                                  Full name
                                </label>
                                <p
                                    id="input-first-name"
                                    className="form-control form-control-alternative">
                                  {user.fullName}
                                </p>
                              </div>
                            </div>
                            ))}

                            <div className="col-lg-6">
                              <div className="form-group focused">
                                <label
                                  className="form-control-label"
                                  htmlFor="input-age"
                                >
                                  Age
                                </label>
                                <p
                                  id="input-age"
                                  className="form-control form-control-alternative">
                                  {user.age}
                                </p>
                              </div>
                            </div>
                          </div>
                        </div>
                        <hr className="my-4" />
                        {/* Address */}
                        <h6 className="heading-small text-muted mb-4">
                          Contact information
                        </h6>
                        <div className="pl-lg-4">
                          { currentUser == null ? (<span></span>) : (
                              user.username !== currentUser.username ? (
                                <span></span>) : (
                                <div className="row">
                                  <div className="col-md-12">
                                    <div className="form-group focused">
                                      <label
                                          className="form-control-label"
                                          htmlFor="input-address"
                                      >
                                        Address
                                      </label>
                                      <p
                                          id="input-first-name"
                                          className="form-control form-control-alternative">
                                        {user.address}
                                      </p>
                                    </div>
                                  </div>
                                </div>
                              ))}

                          <div className="row">
                            <div className="col-lg-4">
                              <div className="form-group focused">
                                <label
                                  className="form-control-label"
                                  htmlFor="input-city"
                                >
                                  City
                                </label>
                                <p
                                  id="input-first-name"
                                  className="form-control form-control-alternative">
                                  {user.city}
                                </p>
                              </div>
                            </div>
                            <div className="col-lg-4">
                              <div className="form-group focused">
                                <label
                                  className="form-control-label"
                                  htmlFor="input-country"
                                >
                                  Country
                                </label>
                                <p
                                  id="input-first-name"
                                  className="form-control form-control-alternative">
                                  {user.country}
                                </p>
                              </div>
                            </div>
                            <div className="col-lg-4">

                            </div>
                          </div>
                        </div>

                      </form>
                    </div>
                  </div>
                </form>
            </div>
          </div>
          <a onClick={() => history.push(`/profile/${user.username}/posts/`)} className="btn btn-info align-middle ms-3 mt-3 mb-3 text-color: white">
            See this user's posts
          </a>
        </div>
      </div>
    </>
  );

}



